        emailThue = findViewById(R.id.email_thue);
        sdtThue = findViewById(R.id.sdt_thue);
        diaChiThue = findViewById(R.id.dia_chi_thue);
        matKhauDangKyThue = findViewById(R.id.mat_khau_dang_ky_thue);
        xacNhanMatKhauThue = findViewById(R.id.xac_nhan_mat_khau_thue);
        dongYDieuKhoanThue = findViewById(R.id.dong_y_dieu_khoan_thue);
        btnDangKyThue = findViewById(R.id.btn_dang_ky_thue);
        chuyenChuTroDangKyThue = findViewById(R.id.chuyen_chu_tro_dang_ky_thue);
        dangNhapThue = findViewById(R.id.dang_nhap_thue);

        btnDangKyThue.setOnClickListener(v -> xuLyDangKy());

        chuyenChuTroDangKyThue.setOnClickListener(v -> {
            Intent intent = new Intent(DangKyNguoiThueActivity.this, DangKyChuTroActivity.class);
            startActivity(intent);
            finish();
        });

        dangNhapThue.setOnClickListener(v -> {
            Intent intent = new Intent(DangKyNguoiThueActivity.this, LoginActivity.class);
            intent.putExtra("targetRole", "tenant");
            startActivity(intent);
            finish();
        });
    }

    private void xuLyDangKy() {
        String hoTen = hoTenThue.getText().toString().trim();
        String email = emailThue.getText().toString().trim();
        String sdt = sdtThue.getText().toString().trim();
        String diaChi = diaChiThue.getText().toString().trim();
        String matKhau = matKhauDangKyThue.getText().toString().trim();
        String xacNhanMatKhau = xacNhanMatKhauThue.getText().toString().trim();
        boolean dongY = dongYDieuKhoanThue.isChecked();

        if (hoTen.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập họ và tên", Toast.LENGTH_SHORT).show();
            return;
        }

        if (email.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập email", Toast.LENGTH_SHORT).show();
            return;
        }

        if (!email.contains("@")) {
            Toast.makeText(this, "Email không hợp lệ", Toast.LENGTH_SHORT).show();
            return;
        }

        if (sdt.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập số điện thoại", Toast.LENGTH_SHORT).show();
            return;
        }

        if (sdt.length() < 10) {
            Toast.makeText(this, "Số điện thoại phải có ít nhất 10 chữ số", Toast.LENGTH_SHORT).show();
            return;
        }

        if (diaChi.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập địa chỉ", Toast.LENGTH_SHORT).show();
            return;
        }

        if (matKhau.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập mật khẩu", Toast.LENGTH_SHORT).show();
            return;
        }

        if (matKhau.length() < 6) {
            Toast.makeText(this, "Mật khẩu phải có ít nhất 6 ký tự", Toast.LENGTH_SHORT).show();
            return;
        }

        if (!matKhau.equals(xacNhanMatKhau)) {
            Toast.makeText(this, "Mật khẩu không khớp", Toast.LENGTH_SHORT).show();
            return;
        }

        if (!dongY) {
            Toast.makeText(this, "Vui lòng đồng ý với Điều khoản dịch vụ", Toast.LENGTH_SHORT).show();
            return;
        }

        Toast.makeText(this, "Đăng ký thành công!", Toast.LENGTH_SHORT).show();

        Intent intent = new Intent(DangKyNguoiThueActivity.this, SplashActivity.class);
        startActivity(intent);
        finish();
    }
}

package com.example.QuanLyPhongTro_App.ui.auth;

import android.content.Intent;
import android.os.Bundle;
import android.widget.Button;
import android.widget.EditText;
import android.widget.TextView;
import android.widget.Toast;

import androidx.appcompat.app.AppCompatActivity;

import com.example.QuanLyPhongTro_App.R;
import com.example.QuanLyPhongTro_App.ui.tenant.MainActivity;
import com.example.QuanLyPhongTro_App.utils.SessionManager;

public class DangNhapChuTroActivity extends AppCompatActivity {

    private EditText emailSdtChuTro;
    private EditText matKhauChuTro;
    private Button btnDangNhapChuTro;
    private Button btnGoogleChuTro;
    private TextView quenMatKhauChuTro;
    private TextView chuyenNguoiThueChuTro;
    private TextView dangKyChuTro;
    private SessionManager sessionManager;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_login);

        sessionManager = new SessionManager(this);

        if (getSupportActionBar() != null) {
            getSupportActionBar().hide();
        }

        emailSdtChuTro = findViewById(R.id.email_sdt);
        matKhauChuTro = findViewById(R.id.mat_khau);
        btnDangNhapChuTro = findViewById(R.id.btn_dang_nhap);
        btnGoogleChuTro = findViewById(R.id.btn_google);
        quenMatKhauChuTro = findViewById(R.id.quen_mat_khau);
        chuyenNguoiThueChuTro = findViewById(R.id.chuyen_vai_tro);
        dangKyChuTro = findViewById(R.id.dang_ky);

        btnDangNhapChuTro.setOnClickListener(v -> xuLyDangNhap());

        btnGoogleChuTro.setOnClickListener(v -> xuLyDangNhapGoogle());

        quenMatKhauChuTro.setOnClickListener(v -> {
            Toast.makeText(this, "Chức năng quên mật khẩu đang phát triển", Toast.LENGTH_SHORT).show();
        });

        chuyenNguoiThueChuTro.setOnClickListener(v -> {
            Intent intent = new Intent(DangNhapChuTroActivity.this, DangNhapNguoiThueActivity.class);
            startActivity(intent);
            finish();
        });

        dangKyChuTro.setOnClickListener(v -> {
            Intent intent = new Intent(DangNhapChuTroActivity.this, DangKyChuTroActivity.class);
            startActivity(intent);
        });
    }

    private void xuLyDangNhap() {
        String emailSdt = emailSdtChuTro.getText().toString().trim();
        String matKhau = matKhauChuTro.getText().toString().trim();

        if (emailSdt.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập email hoặc số điện thoại", Toast.LENGTH_SHORT).show();
            return;
        }

        if (matKhau.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập mật khẩu", Toast.LENGTH_SHORT).show();
            return;
        }

        if (matKhau.length() < 6) {
            Toast.makeText(this, "Mật khẩu phải có ít nhất 6 ký tự", Toast.LENGTH_SHORT).show();
            return;
        }

        String userId = "landlord_" + System.currentTimeMillis();
        String userName = emailSdt.contains("@") ? emailSdt.split("@")[0] : "Chủ trọ";

        sessionManager.createLoginSession(userId, userName, emailSdt, "landlord");

        Toast.makeText(this, "Đăng nhập thành công!", Toast.LENGTH_SHORT).show();

        Intent intent = new Intent(DangNhapChuTroActivity.this, MainActivity.class);
        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
        startActivity(intent);
        finish();
    }

    private void xuLyDangNhapGoogle() {
        Toast.makeText(this, "Chức năng Google Sign-In đang phát triển", Toast.LENGTH_SHORT).show();
    }
}

