public class DangNhapNguoiThueActivity extends AppCompatActivity {

    private EditText emailSdtThue;
    private EditText matKhauThue;
    private Button btnDangNhapThue;
    private Button btnGoogleThue;
    private TextView quenMatKhauThue;
    private TextView chuyenChuTroThue;
    private TextView dangKyThue;
    private SessionManager sessionManager;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_tenant_login);

        sessionManager = new SessionManager(this);

        // Ẩn ActionBar
        if (getSupportActionBar() != null) {
            getSupportActionBar().hide();
        }

        // Ánh xạ các view
        emailSdtThue = findViewById(R.id.email_sdt_thue);
        matKhauThue = findViewById(R.id.mat_khau_thue);
        btnDangNhapThue = findViewById(R.id.btn_dang_nhap_thue);
        btnGoogleThue = findViewById(R.id.btn_google_thue);
        quenMatKhauThue = findViewById(R.id.quen_mat_khau_thue);
        chuyenChuTroThue = findViewById(R.id.chuyen_chu_tro_thue);
        dangKyThue = findViewById(R.id.dang_ky_thue);

        // Xử lý nút Đăng Nhập
        btnDangNhapThue.setOnClickListener(v -> xuLyDangNhap());

        // Xử lý nút Google
        btnGoogleThue.setOnClickListener(v -> xuLyDangNhapGoogle());

        // Xử lý Quên mật khẩu
        quenMatKhauThue.setOnClickListener(v -> {
            Toast.makeText(this, "Chức năng quên mật khẩu đang phát triển", Toast.LENGTH_SHORT).show();
        });

        // Xử lý Chuyển sang Chủ Trọ
        chuyenChuTroThue.setOnClickListener(v -> {
            Intent intent = new Intent(DangNhapNguoiThueActivity.this, DangNhapChuTroActivity.class);
            startActivity(intent);
            finish();
        });

        // Xử lý Đăng Ký
        dangKyThue.setOnClickListener(v -> {
            Intent intent = new Intent(DangNhapNguoiThueActivity.this, DangKyNguoiThueActivity.class);
            startActivity(intent);
        });
    }

    // Hàm xử lý đăng nhập
    private void xuLyDangNhap() {
        String emailSdt = emailSdtThue.getText().toString().trim();
        String matKhau = matKhauThue.getText().toString().trim();

        // Kiểm tra dữ liệu
        if (emailSdt.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập email hoặc số điện thoại", Toast.LENGTH_SHORT).show();
            return;
        }

        if (matKhau.isEmpty()) {
            Toast.makeText(this, "Vui lòng nhập mật khẩu", Toast.LENGTH_SHORT).show();
            return;
        }

        if (matKhau.length() < 6) {
            Toast.makeText(this, "Mật khẩu phải có ít nhất 6 ký tự", Toast.LENGTH_SHORT).show();
            return;
        }

        // TODO: Gửi dữ liệu lên server để xác thực
        // Giả lập đăng nhập thành công
        String userId = "user_" + System.currentTimeMillis();
        String userName = emailSdt.contains("@") ? emailSdt.split("@")[0] : "Người dùng";

        // Lưu session
        sessionManager.createLoginSession(userId, userName, emailSdt, "tenant");

        Toast.makeText(this, "Đăng nhập thành công!", Toast.LENGTH_SHORT).show();

        // Chuyển về MainActivity với session đã đăng nhập
        Intent intent = new Intent(DangNhapNguoiThueActivity.this, MainActivity.class);
        intent.setFlags(Intent.FLAG_ACTIVITY_NEW_TASK | Intent.FLAG_ACTIVITY_CLEAR_TASK);
        startActivity(intent);
        finish();
    }

    // Hàm xử lý đăng nhập Google
    private void xuLyDangNhapGoogle() {
        Toast.makeText(this, "Chức năng Google Sign-In đang phát triển", Toast.LENGTH_SHORT).show();
    }
}

